#!/usr/bin/env node

'use strict';

var program = require('commander');
var noisegen = require('..');

program
  .version(require('../package').version)
  .option('-l, --length <bytes>', 'Bytes to generate (default: Infinity)')
  .option('-s, --size <bytes>', 'Bytes in each random chunk (default: 512)')
  .parse(process.argv);

noisegen({
  length: program.length ? Number(program.length) : noisegen.DEFAULTS.length,
  size: program.size ? Number(program.size) : noisegen.DEFAULTS.size,
}).pipe(process.stdout);
